local DungeonBtn=Instance.new("TextButton")DungeonBtn.Size=UDim2.new(1,-20,0,45)DungeonBtn.Position=UDim2.new(0,10,0.84,60)DungeonBtn.BackgroundColor3=Color3.fromRGB(255,0,100)DungeonBtn.Text="Auto Dungeon" DungeonBtn.TextColor3=Color3.new(1,1,1)DungeonBtn.Font=Enum.Font.GothamBold DungeonBtn.TextScaled=true DungeonBtn.Parent=Frm
DungeonBtn.Activated:Connect(function()Togs.AutoDungeon=not Togs.AutoDungeon spawn(function()while Togs.AutoDungeon do pcall(function()-- Find enter for _,r in pairs(RS:GetDescendants())do if r:IsA("RemoteEvent")and string.find(r.Name:lower(),"dungeon|raid|enter")then r:FireServer("Enter")end end -- Prompts for _,v in pairs(WS:GetDescendants())do if v:IsA("ProximityPrompt")and string.find(v.Name:lower(),"dungeon")then fireproximityprompt(v)end end end)wait(5)-- Farm inside + reenter end end)print("Auto Dungeon: "..tostring(Togs.AutoDungeon))end)
local BossBtn=Instance.new("TextButton")BossBtn.Size=UDim2.new(1,-20,0,45)BossBtn.Position=UDim2.new(0,10,0.91,60)BossBtn.BackgroundColor3=Color3.fromRGB(200,0,255)BossBtn.Text="Auto Boss" BossBtn.TextColor3=Color3.new(1,1,1)BossBtn.Font=Enum.Font.GothamBold BossBtn.TextScaled=true BossBtn.Parent=Frm
BossBtn.Activated:Connect(function()Togs.AutoBoss=not Togs.AutoBoss spawn(function()while Togs.AutoBoss do pcall(function()local Root=GetRoot()if Root then local boss=nil local maxH=0 for _,obj in pairs(WS:GetDescendants())do if obj:IsA("Humanoid")and obj.MaxHealth>1e6 and obj.Health>0 and obj.Parent:FindFirstChild("HumanoidRootPart")then local dist=(Root.Position-obj.Parent.HumanoidRootPart.Position).Magnitude if dist<100 then boss=obj.Parent.HumanoidRootPart maxH=obj.MaxHealth break end end end if boss then Root.CFrame=boss.CFrame*CFrame.new(0,5,0)-- Focus kill end end end)wait(0.1)end end)print("Auto Boss: "..tostring(Togs.AutoBoss))end)
